package com.usit.hub4tickets.utilsimport android.content.Contextimport android.support.v7.widget.RecyclerViewimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.CheckedTextViewimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.search.model.CommonSelectorPojo/** * Created by Bhagyashri Burade * Date: 24/10/2018 * Email: bhagyashri.burade@usit.net.in */class SignleSelectionSortAdapter(    internal var context: Context,    private val dataList: ArrayList<CommonSelectorPojo>,    private var apply: Boolean,    private var listener: OnClickListener) : RecyclerView.Adapter<SignleSelectionSortAdapter.SortViewHolder>() {    private var selectedPosition = 0    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): SortViewHolder {        val itemView = LayoutInflater.from(parent.context).inflate(R.layout.single_selection_layout, parent, false)        return SortViewHolder(itemView)    }    override fun onBindViewHolder(holder: SignleSelectionSortAdapter.SortViewHolder, position: Int) {        holder.checkBox.isChecked = dataList[position].isSelected == apply        holder.checkBox.text = dataList[position].itemsName        holder.checkBox.setOnClickListener {            for (k in dataList.indices) {                dataList[k].isSelected = k == position                if (k == position)                    selectedPosition = position            }            notifyDataSetChanged()            listener.onListItemClick(dataList, selectedPosition)        }    }    interface OnClickListener {        fun onListItemClick(commonSelectorPojo: ArrayList<CommonSelectorPojo>, position: Int)    }    override fun getItemCount(): Int {        return dataList.size    }    inner class SortViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var checkBox: CheckedTextView = itemView.findViewById(R.id.checked_text_item) as CheckedTextView    }}