package com.usit.hub4tickets.flight.uiimport android.widget.Filterimport com.usit.hub4tickets.search.AirportsSearchAdapterimport com.usit.hub4tickets.search.model.AirportsSelectorPojo/** * Created by Bhagyashri Burade * Date: 23/11/2018 * Email: bhagyashri.burade@usit.net.in */class CustomFilterAirportsNew(    private var filterList: ArrayList<AirportsSelectorPojo>,    private var adapter: AirportsSearchAdapter) :    Filter() {    //FILTERING    override fun performFiltering(constraint: CharSequence?): FilterResults {        var constraint = constraint        //RESULTS        val results = FilterResults()        //VALIDATION        if (constraint != null && constraint.isNotEmpty()) {            //CHANGE TO UPPER FOR CONSISTENCY            constraint = constraint.toString().toUpperCase()            val filteredList: ArrayList<AirportsSelectorPojo> = ArrayList()            for (i in 0 until filterList.size) {                //FILTER                if ((filterList[i].airPortCode?.toUpperCase()?.contains(constraint)!! || filterList[i].airPortCountry?.toUpperCase()?.contains(                        constraint                    )!!) && filterList[i].airPortNameAndCode?.toUpperCase()?.contains(                        constraint                    )!!                ) {                    filteredList.add(filterList[i])                }            }            filteredList.distinct()            results.count = filteredList.size            results.values = filteredList        } else {            results.count = filterList.size            results.values = filterList        }        return results    }    //PUBLISH RESULTS    override fun publishResults(constraint: CharSequence, results: FilterResults) {        if (null != results.values)            adapter.temArrayListCommonSelector = results.values as ArrayList<AirportsSelectorPojo>        adapter.notifyDataSetChanged()    }}