package com.usit.hub4tickets.flight.uiimport android.os.Bundleimport android.support.v4.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.flight.adapter.FlightViewPagerAdapterimport kotlinx.android.synthetic.main.common_toolbar.*import kotlinx.android.synthetic.main.fragment_flight_main.*class FlightMainFragment : Fragment() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        retainInstance = true    }    private var flightViewPagerAdapter: FlightViewPagerAdapter? = null    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.fragment_flight_main, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initTootlbar()        setViewPagerAdapter()    }    private fun setViewPagerAdapter() {        flightViewPagerAdapter = FlightViewPagerAdapter(resources, childFragmentManager)        viewPager.adapter = flightViewPagerAdapter        viewPager.offscreenPageLimit = 3        tabs.setupWithViewPager(viewPager)    }    private fun initTootlbar() {        titleToolBar?.text = resources.getString(R.string.flight)        mainToolbar?.setNavigationOnClickListener { onBackPressed() }    }    companion object {        @JvmStatic        fun newInstance() =            FlightMainFragment().apply {                arguments = Bundle().apply {                }            }    }    fun onBackPressed() {        activity?.onBackPressed()    }}