package com.usit.hub4tickets.flight.uiimport android.app.Activityimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AlertDialogimport android.support.v7.widget.LinearLayoutManagerimport android.text.TextUtilsimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.EditTextimport com.usit.hub4tickets.MainApplicationimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.addremovelist.ListAdapterimport com.usit.hub4tickets.common.RootFragmentimport com.usit.hub4tickets.domain.presentation.presenters.FlightPresenterimport com.usit.hub4tickets.domain.presentation.screens.main.FlightPresenterImplimport com.usit.hub4tickets.flight.model.FlightViewModelimport com.usit.hub4tickets.search.FlightSearchActivityimport com.usit.hub4tickets.search.model.CommonSelectorPojoimport com.usit.hub4tickets.utils.*import com.usit.hub4tickets.utils.view.dialog.CustomDialogPresenterimport kotlinx.android.synthetic.main.fragment.*import kotlinx.android.synthetic.main.search_layout.*import kotlinx.android.synthetic.main.sort_by_dialog.view.*import java.util.*import kotlin.collections.ArrayListclass FragmentMultiCity : RootFragment(), FlightPresenter.MainView, ListAdapter.OnItemClickListener {    private var isItemClicked: Boolean = false    private var isItemClickedTo: Boolean = false    private var fromCode: String? = null    private var toCode: String? = null    private var travelClassCode: String? = "ECONOMY"    private var travelClass: String? = "Economy"    private val FROM_SELECTION_REQUEST = 501    private val TO_SELECTION_REQUEST = 502    private lateinit var model: FlightViewModel    private lateinit var presenter: FlightPresenter    var listAdapter: ListAdapter? = null    override fun doRetrieveModel(): FlightViewModel = this.model    override fun showState(viewState: FlightPresenter.MainView.ViewState) {        when (viewState) {            FlightPresenter.MainView.ViewState.IDLE -> {                Utility.showProgress(false, context)            }            FlightPresenter.MainView.ViewState.LOADING -> {                Utility.showProgress(true, context)            }            FlightPresenter.MainView.ViewState.MULTICITY_DETAILS_SUCCESS -> {                showList(model.multiCityListViewModel.responseData as ArrayList<FlightViewModel.ResponseDataMulticity>?)            }            FlightPresenter.MainView.ViewState.FLIGHT_NOT_FOUND            -> {            }            FlightPresenter.MainView.ViewState.ERROR            -> {                Utility.showCustomDialog(context, doRetrieveModel().errorMessage.message, "", null)            }        }    }    private fun showList(        responseData: ArrayList<FlightViewModel.ResponseDataMulticity>?    ) {        val intent = Intent(context, MulticitySearchListActivity::class.java)        intent.putExtra(Constant.Path.MULTICITY_DETAILS, responseData)        intent.putExtra(Constant.Path.CABIN_CLASS, travelClass)        startActivity(intent)    }    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {        return inflater.inflate(R.layout.fragment_multicity, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        var list: ArrayList<FlightViewModel.MultiCitiesForSearch>? = ArrayList()        list?.add(FlightViewModel.MultiCitiesForSearch("", "", ""))        list?.add(FlightViewModel.MultiCitiesForSearch("", "", ""))        listAdapter = ListAdapter(list, this, this)        val layoutManager = LinearLayoutManager(context)        recycler_view.layoutManager = layoutManager        recycler_view.adapter = listAdapter        init()        setDataAndListeners(view)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        retainInstance = true    }    override fun onPause() {        super.onPause()        edt_from.error = null//removes error        edt_to.error = null    }    private fun openSearchActivityFlightReturn(        title: String,        selectionRequest: Int,        flag: String    ) {        val intent = Intent(context, FlightSearchActivity::class.java)        intent.putExtra(Constant.Path.ACTIVITY_TITLE, title)        intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP)        intent.putExtra(Constant.Path.FLAG, flag)        startActivityForResult(intent, selectionRequest)    }    private fun attemptSearch(listM: ArrayList<FlightViewModel.MultiCitiesForSearch>) {        // Reset errors.        edt_from.error = null        edt_to.error = null        //val departureDate = tv_departure.text.toString()        var cancel = false        var focusView: View? = null        if (TextUtils.isEmpty(fromEditView?.text)) {            edt_from.error = getString(R.string.error_field_required_from_airport)            focusView = fromEditView            cancel = true        } else if (TextUtils.isEmpty(fromEditView?.text)) {            edt_to.error = getString(R.string.error_field_required_to_airport)            focusView = fromEditView            cancel = true        } else if (fromCode.equals(toCode)) {            CustomDialogPresenter.showDialog(                context,                "",                getString(R.string.error_field_same_location),                context!!.resources.getString(                    R.string.ok                ),                null,                null            )            cancel = true        }        if (cancel) {            focusView?.requestFocus()        } else {            callFlightDetailsApi(listM)        }    }    private var currency: String? = ""    private fun callFlightDetailsApi(listM: ArrayList<FlightViewModel.MultiCitiesForSearch>) {        currency = Pref.getValue(context, PrefConstants.CURRENCY_DEFAULT, "")        presenter.callMulticityDetails(            adults!!,            children!!,            currency!!,            Pref.getValue(context, PrefConstants.USER_ID, "0").toString(),            infants!!,            "in-EN",            listM,            travelClassCode.toString(),//ECONOMY, PREMIUM_ECONOMY, BUSINESS, FIRST\            Pref.getValue(context, PrefConstants.USER_ID, "0").toString()        )    }    private var adults: String? = "1"    private var children: String? = "0"    private var infants: String? = "0"    private var adapterPosition: Int = 0    private var adapterApplyPosition: Int = 0    private val listM: ArrayList<FlightViewModel.MultiCitiesForSearch> = ArrayList()    private fun selectTravelClass() {        val dialogBuilder = AlertDialog.Builder(this.context!!).create()        val inflater = this.layoutInflater        val dialogView = inflater.inflate(R.layout.sort_by_dialog, null)        dialogView.ll_passenger_info.visibility = View.VISIBLE        dialogView.ll_apply.visibility = View.VISIBLE        dialogView.tv_dialog_header.text = getString(R.string.passenger_information)        dialogView.tv_dialog_header_rcv.text = getString(R.string.cabin_class)        dialogView.tv_quantity_adult.text = adults        dialogView.tv_quantity_children.text = children        dialogView.tv_quantity_infants.text = infants        dialogView.imv_minus_adult.setOnClickListener {            Utility.onMinusClick(dialogView.tv_quantity_adult, true, false, dialogView.tv_quantity_infants)        }        dialogView.imv_plus_adult.setOnClickListener {            Utility.onAddClick(dialogView.tv_quantity_adult, true, false, "")        }        dialogView.imv_minus_children.setOnClickListener {            Utility.onMinusClick(dialogView.tv_quantity_children, false, false, null)        }        dialogView.imv_plus_children.setOnClickListener {            Utility.onAddClick(dialogView.tv_quantity_children, false, false, "")        }        dialogView.imv_minus_infants.setOnClickListener {            Utility.onMinusClick(                dialogView.tv_quantity_infants,                false,                true,                null            )        }        dialogView.imv_plus_infants.setOnClickListener {            Utility.onAddClick(                dialogView.tv_quantity_infants,                false,                true,                dialogView.tv_quantity_adult.text.toString()            )        }        dialogView.button_dialog_apply.setOnClickListener {            adults = dialogView.tv_quantity_adult.text.toString()            children = dialogView.tv_quantity_children.text.toString()            infants = dialogView.tv_quantity_infants.text.toString()            btn_passengers.text = dialogView.tv_quantity_adult.text.toString() + " Adult " +                    dialogView.tv_quantity_children.text.toString() + " Children " +                    dialogView.tv_quantity_infants.text.toString() + " Infants "            btn_class.text = travelClass            attemptSearch(listM)            dialogBuilder.dismiss()        }        dataListTravelClass!!.get(adapterPosition).isSelected = true        val layoutManager = LinearLayoutManager(context)        dialogView.selection_list?.layoutManager = layoutManager        val adapter = SignleSelectionAdapter(            this.context!!,            dataListTravelClass!!, true,            object : SignleSelectionAdapter.OnClickListener {                override fun onListItemClick(                    dataList: ArrayList<CommonSelectorPojo>,                    position: Int                ) {                    adapterPosition = position                    travelClassCode = dataList[position].code                    travelClass = dataList[position].itemsName                }            })        dialogView.button_dialog_cancel.setOnClickListener {            dataListTravelClass!!.get(adapterPosition).isSelected = false            adapter.notifyDataSetChanged()            dialogBuilder.dismiss()        }        dialogView.selection_list.adapter = adapter        dialogBuilder.setView(dialogView)        dialogBuilder.show()    }    private var dataListTravelClass: ArrayList<CommonSelectorPojo>? = null    private fun init() {        this.model = FlightViewModel(context)        this.presenter = FlightPresenterImpl(this, context)        dataListTravelClass = ArrayList()        //travel class //ECONOMY, PREMIUM_ECONOMY, BUSINESS, FIRST        dataListTravelClass?.add(            CommonSelectorPojo(                "1",                getString(R.string.economy_text),                getString(R.string.economy_code),                true            )        )        dataListTravelClass?.add(            CommonSelectorPojo(                "2",                getString(R.string.pre_economy_text),                getString(R.string.pre_economy_code),                false            )        )        dataListTravelClass?.add(            CommonSelectorPojo(                "3",                getString(R.string.business_text),                getString(R.string.business_code),                false            )        )        dataListTravelClass?.add(            CommonSelectorPojo(                "4",                getString(R.string.first_text),                getString(R.string.first_code),                false            )        )    }    private fun setDataAndListeners(v: View) {        btn_class.setOnClickListener { selectTravelClass() }        btn_class.text = travelClass        btn_passengers.text = Utility.showPassengersAdult(adults).toString() +                Utility.showPassengersChildren(children).toString() +                Utility.showPassengersInfants(infants).toString()        btn_passengers.setOnClickListener { selectTravelClass() }        im_btn_search.setOnClickListener {            listM.clear()            listM.addAll(listAdapter?.getSearchParamList()!!)            for (list in listAdapter?.getSearchParamList()?.indices!!) {                Log.d("date_from ===", listM[list].date_from)                Log.d("fly_to ===", listM[list].fly_to)                Log.d("fly_from ===", listM[list].fly_from)            }            attemptSearch(listM)        }    }    override fun onDepartureDateClick() {        Utility.dateDialogWithMinMaxDate(Calendar.getInstance(), activity, tv_departure, 0)    }    var fromEditView: EditText? = null    var toEditView: EditText? = null    var position: Int = 0    override fun onFromClick(edtTo: EditText, edtFrom: EditText, position: Int) {        if (MainApplication.getInstance.isConnected()) {            //Utility.hideSoftKeyboard(v)            openSearchActivityFlightReturn(                this.javaClass.simpleName.toString(),                FROM_SELECTION_REQUEST,                Constant.Path.FROM            )            fromEditView = edtFrom            toEditView = edtTo            this.position = position        } else {            doRetrieveModel().errorMessage.message =                doRetrieveModel().context!!.getString(R.string.message_no_internet)            showState(FlightPresenter.MainView.ViewState.ERROR)        }    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        super.onActivityResult(requestCode, resultCode, data)        when (requestCode) {            FROM_SELECTION_REQUEST -> {                if (resultCode == Activity.RESULT_OK) {                    isItemClicked = true                    fromCode = data?.getStringExtra(PrefConstants.SELECTED_ITEMS_TYPE)                    fromEditView?.setText(                        data?.getStringExtra(PrefConstants.SELECTED_ITEMS_NAME)                    )                    if (fromEditView?.text!!.toString().equals(toEditView?.text.toString())) {                        edt_from.setText("")                        CustomDialogPresenter.showDialog(                            context,                            "",                            getString(R.string.error_field_same_location),                            context!!.resources.getString(                                R.string.ok                            ),                            null,                            null                        )                    }                }            }            TO_SELECTION_REQUEST -> {                if (resultCode == Activity.RESULT_OK) {                    isItemClickedTo = true                    toCode = data?.getStringExtra(PrefConstants.SELECTED_ITEMS_TYPE)                    toEditView?.setText(                        data?.getStringExtra(PrefConstants.SELECTED_ITEMS_NAME)                    )                    if (toEditView?.text!!.toString().equals(fromEditView?.text!!.toString())) {                        toEditView?.setText("")                        CustomDialogPresenter.showDialog(                            context,                            "",                            getString(R.string.error_field_same_location),                            context!!.resources.getString(                                R.string.ok                            ),                            null,                            null                        )                    }                }            }        }    }    override fun onToClick(edtTo: EditText, edtFrom: EditText, position: Int) {        if (MainApplication.getInstance.isConnected()) {            //Utility.hideSoftKeyboard(v)            openSearchActivityFlightReturn(                this.javaClass.simpleName.toString(),                TO_SELECTION_REQUEST,                Constant.Path.TO            )            toEditView = edtTo            fromEditView = edtFrom            this.position = position        } else {            doRetrieveModel().errorMessage.message =                doRetrieveModel().context!!.getString(R.string.message_no_internet)            showState(FlightPresenter.MainView.ViewState.ERROR)        }    }    override fun onBackPressed(): Boolean {        return fragmentManager?.popBackStackImmediate()!!    }}