package com.usit.hub4tickets.flight.uiimport android.os.Bundleimport android.support.v7.widget.LinearLayoutManagerimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.domain.presentation.screens.BaseActivityimport com.usit.hub4tickets.flight.adapter.StopDetailsViewMulticityAdapterimport com.usit.hub4tickets.flight.model.FlightViewModelimport com.usit.hub4tickets.utils.Constantimport kotlinx.android.synthetic.main.activity_see_details.*import kotlinx.android.synthetic.main.common_toolbar.*class SeeDetailsMulticityActivity : BaseActivity() {    override fun getLayoutResource(): Int {        return R.layout.common_toolbar    }    private var stopDetailsResponse: FlightViewModel.MultiCityResult? = null    private var stopDetailsResponseArray: ArrayList<FlightViewModel.StopDetailMulticity>? = ArrayList()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_see_details)        init()        if (null != intent.extras) {            stopDetailsResponse = intent.extras.get(Constant.Path.STOP_DETAILS) as FlightViewModel.MultiCityResult            stopDetailsResponseArray =                stopDetailsResponse!!.stopDetails as ArrayList<FlightViewModel.StopDetailMulticity>            trip_title.text = stopDetailsResponse?.fromCity + " - " + stopDetailsResponse?.toCity        }        val layoutManager = LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false)        recycler_view_see_details!!.layoutManager = layoutManager        recycler_view_see_details!!.adapter = StopDetailsViewMulticityAdapter(stopDetailsResponseArray)    }    private fun init() {        title = resources.getString(R.string.trip_stop_details)        mainToolbar.setNavigationOnClickListener { onBackPressed() }    }}