package com.usit.hub4tickets.hotelimport android.os.Bundleimport android.support.v4.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.flight.ui.FlightMainFragmentimport kotlinx.android.synthetic.main.common_toolbar.*import kotlinx.android.synthetic.main.search_hotels_layout.*class HotelsMainFragment : Fragment() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        retainInstance = true    }    private var hotelsMainFragment: HotelsMainFragment? = null    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.fragment_hotel_main, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initTootlbar()    }    private fun initTootlbar() {        titleToolBar?.text = resources.getString(R.string.hotels)        mainToolbar?.setNavigationOnClickListener { onBackPressed() }        im_btn_hotel_search.setOnClickListener {            fragmentManager?.beginTransaction()?.replace(R.id.frame_container, HotelsListFragment())                ?.addToBackStack("HotelsListFragment")                ?.commit()        }    }    fun onBackPressed() {        activity?.onBackPressed()    }}