package com.usit.hub4tickets.hotelimport android.content.Intentimport android.os.Bundleimport android.support.design.widget.BottomNavigationViewimport android.support.v4.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.usit.hub4tickets.Rimport com.usit.hub4tickets.dashboard.ui.DashboardActivityimport kotlinx.android.synthetic.main.fragment_flight_main.*class HotelsMainFragment : Fragment() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        retainInstance = true    }    private val mOnNavigationItemSelectedListener = BottomNavigationView.OnNavigationItemSelectedListener { item ->        when (item.itemId) {            R.id.navigation_home -> {                val intent = Intent(activity?.baseContext, DashboardActivity::class.java)                intent.putExtra("SCREEN_NAME", "home")                startActivity(intent)                activity?.finish()            }            R.id.navigation_my_account -> {                val intent = Intent(activity?.baseContext, DashboardActivity::class.java)                intent.putExtra("SCREEN_NAME", "account")                startActivity(intent)                activity?.finish()            }            R.id.navigation_help -> {                val intent = Intent(activity?.baseContext, DashboardActivity::class.java)                intent.putExtra("SCREEN_NAME", "help")                startActivity(intent)                activity?.finish()            }        }        return@OnNavigationItemSelectedListener true    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        // Inflate the layout for this fragment        return inflater.inflate(R.layout.fragment_home_main, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        navigation_flight.setOnNavigationItemSelectedListener(mOnNavigationItemSelectedListener)    }    fun onBackPressed(): Boolean {       return fragmentManager?.popBackStackImmediate()!!    }}